import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import LensPopup from "../components/LensPopup";

export default function RefractiveLensPage() {
  const navigate = useNavigate();
  const [popupBrand, setPopupBrand] = useState(null);
  const [selectedRows, setSelectedRows] = useState([]);
  const [nextBrand, setNextBrand] = useState(null);

  const handleBrandClick = (brand) => {
    if (popupBrand === brand) {
      setPopupBrand(null); // Îã´Í∏∞
    } else if (popupBrand) {
      // Îã§Î•∏ ÌåùÏóÖÏù¥ Ïó¥Î†§ÏûàÏúºÎ©¥ Î®ºÏ†Ä Îã´Í≥† ÏùºÏ†ï ÏãúÍ∞Ñ Îí§Ïóê Ï†ÑÌôò
      setPopupBrand(null);
      setNextBrand(brand);
    } else {
      setPopupBrand(brand);
    }
  };

  useEffect(() => {
    if (!popupBrand && nextBrand) {
      const timer = setTimeout(() => {
        setPopupBrand(nextBrand);
        setNextBrand(null);
      }, 300); // fade-out ÏòàÏÉÅ ÏãúÍ∞Ñ
      return () => clearTimeout(timer);
    }
  }, [popupBrand, nextBrand]);

  const handleClosePopup = () => {
    setPopupBrand(null);
  };

  const handleRowSelect = (row) => {
    setSelectedRows((prev) =>
      prev.includes(row) ? prev.filter(r => r !== row) : [...prev, row]
    );
  };

  return (
    <div className="bg-slate-900 text-white min-h-screen p-6 relative">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-bold">Ïù¥ÎÖ∏Ìã∞ÏïàÍ≤Ω Ïù¥Ï≤úÏ¶ùÌè¨Ï†ê</h1>
        <div className="flex gap-2">
          <button className="text-sm underline" onClick={() => navigate(-1)}>‚Üê Îí§Î°ú</button>
          <button className="text-sm underline" onClick={() => navigate("/")}>üè† Ìôà</button>
        </div>
      </div>
      <h2 className="text-xl font-semibold mb-4">Íµ¥Ï†àÏù¥ÏÉÅÍµêÏ†ïÎ†åÏ¶à Í∞ÄÍ≤© ÎπÑÍµê</h2>

      <table className="w-full text-center border-collapse border border-gray-500">
        <thead className="bg-slate-700">
          <tr>
            <th className="border p-2">Íµ¥Ï†àÎ•†</th>
            <th className="border p-2 cursor-pointer" onClick={() => handleBrandClick("chemi2")}>ÏºÄÎØ∏ 2ÏÑ∏ÎåÄ üëç<div className="text-sm">Í∞ÄÏÑ±ÎπÑ Ï∂îÏ≤ú</div></th>
            <th className="border p-2 cursor-pointer" onClick={() => handleBrandClick("chemi3")}>ÏºÄÎØ∏ 3ÏÑ∏ÎåÄ IR ‚≠ê<div className="text-sm">Í≥†Í∏∞Îä• Ï∂îÏ≤ú</div></th>
            <th className="border p-2 cursor-pointer text-yellow-300 font-bold" onClick={() => handleBrandClick("hoya")}>Ìò∏Ïïº Îâ¥Îü≠Ïä§ üåü<div className="text-sm">Î≤†Ïä§Ìä∏ Ï∂îÏ≤ú</div></th>
          </tr>
        </thead>
        <tbody>
          <tr>
  <td className="border p-2 cursor-pointer" onClick={() => handleRowSelect("1.56")}>1.56</td>
  <td className={`border p-2 ${selectedRows.includes("1.56") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>3ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.56") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>5ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.56") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>7ÎßåÏõê</td>
</tr>
          <tr>
  <td className="border p-2 cursor-pointer" onClick={() => handleRowSelect("1.60")}>1.60</td>
  <td className={`border p-2 ${selectedRows.includes("1.60") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>5ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.60") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>8ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.60") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>9ÎßåÏõê</td>
</tr>
          <tr>
  <td className="border p-2 cursor-pointer" onClick={() => handleRowSelect("1.67")}>1.67</td>
  <td className={`border p-2 ${selectedRows.includes("1.67") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>8ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.67") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>10ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.67") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>12ÎßåÏõê</td>
</tr>
          <tr>
  <td className="border p-2 cursor-pointer" onClick={() => handleRowSelect("1.74")}>1.74</td>
  <td className={`border p-2 ${selectedRows.includes("1.74") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>10ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.74") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>15ÎßåÏõê</td>
  <td className={`border p-2 ${selectedRows.includes("1.74") ? 'bg-yellow-400 text-black font-bold shadow-md' : selectedRows.length > 0 ? 'opacity-30' : ''}`}>19ÎßåÏõê</td>
</tr>
        </tbody>
      </table>

      {popupBrand && (
        <>
          <div className="fixed top-4 w-full z-50 text-center">{["hoya", "chemi3"].includes(popupBrand) && (
            <p className="text-2xl text-center text-yellow-400 font-semibold animate-pulse mt-2 mb-1">
              üåü Í≥†Í∞ù ÏÑ†ÌÉùÎ•† ÏÉÅÏúÑ Î†åÏ¶à
            </p>)}</div>}
          <LensPopup brand={popupBrand} onClose={handleClosePopup} />
        </>
      )}
    </div>
  );
}
